import React from 'react'

const MemoryField = ({ label, value }) => {
  return(
    <div>
      <label style={{ width: '5em' }}>{label.toString(16).toUpperCase()}</label><input value={value} type="text"/>
    </div>
  )
}

const Memory = () => {
  const memoryStyle = {
    flex: 1, 
    textAlign: 'right',
  }

  let MEM = []
  MEM.length = 4096
  MEM.fill(0)

  return(
    <div style={memoryStyle}>
      <form>
        <MemoryField label="CI"/>
        <MemoryField label="AC"/>
        { MEM.map((value, address) => <MemoryField key={address} label={address} value={value}/>) }
      </form>
    </div>
  )
}

export default Memory
