import React from 'react'

const MemoryField = ({ name }) => {
  return(
    <div>
      <label>{name}</label><input type="text"/>
    </div>
  )
}

const Memory = () => {
  const memoryStyle = {
    flex: 1, 
  }

  let MEM = []
  MEM.length = 4096
  MEM.fill(0)

  return(
    <div style={memoryStyle}>
      <form>
        <MemoryField name="CI"/>
        <MemoryField name="AC"/>
        { MEM.map((value, address) => <MemoryField name={address.toString(16)}/>) }
      </form>
    </div>
  )
}

export default Memory
